mapper.select:
/* 구현 Sql */
SELECT A.BACKUP_RESULT_ID , A.BACKUP_OPERT_ID , B.BACKUP_OPERT_NM , A.BACKUP_FILE , B.BACKUP_ORGINL_DRCTRY , B.BACKUP_STRE_DRCTRY , A.STTUS , C.CODE_NM AS "sttusNm" , A.ERROR_INFO , A.EXECUT_BEGIN_TM AS "executBeginTime" , A.EXECUT_END_TM AS "executEndTime" , A.LAST_UPDT_PNTTM AS "lastUpdusrPnttm" , A.LAST_UPDUSR_ID , A.FRST_REGISTER_ID , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BACKUP_RESULT A LEFT OUTER JOIN COMTN_BACKUP_OPERT B ON A.BACKUP_OPERT_ID = B.BACKUP_OPERT_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM076') C ON A.STTUS = C.CODE WHERE 1=1
AND A.STTUS = #{sttus}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) >= #{searchKeywordFrom}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) <= #{searchKeywordTo}
AND A.BACKUP_OPERT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.BACKUP_OPERT_ID LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.BACKUP_RESULT_ID DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTN_BACKUP_RESULT A LEFT OUTER JOIN COMTN_BACKUP_OPERT B ON A.BACKUP_OPERT_ID = B.BACKUP_OPERT_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM076') C ON A.STTUS = C.CODE WHERE 1=1
AND A.STTUS = #{sttus}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) >= #{searchKeywordFrom}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) <= #{searchKeywordTo}
AND A.BACKUP_OPERT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.BACKUP_OPERT_ID LIKE '%'|| #{searchKeyword} ||'%'

mapper.insert:

            INSERT INTO COMTN_BACKUP_RESULT (
                	BACKUP_RESULT_ID
                  , BACKUP_OPERT_ID
                  , BACKUP_FILE
                  , STTUS
                  , ERROR_INFO
                  , EXECUT_BEGIN_TM
                  , EXECUT_END_TM
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
                    #{backupResultId}
                  , #{backupOpertId}
                  , #{backupFile, jdbcType=VARCHAR}
                  , #{sttus, jdbcType=VARCHAR}
                  , #{errorInfo, jdbcType=VARCHAR}
                  , #{executBeginTime, jdbcType=VARCHAR}
                  , #{executEndTime, jdbcType=VARCHAR}
				  , #{frstRegisterId}
				  , now()
				  , #{frstRegisterId} 
				  , now()
            )

mapper.update:

UPDATE COMTN_BACKUP_RESULT 
   SET BACKUP_FILE 		= #{backupFile, jdbcType=VARCHAR}
     , ERROR_INFO 		= #{errorInfo, jdbcType=VARCHAR}
     , STTUS 			= #{sttus, jdbcType=VARCHAR}
     , EXECUT_END_TM 	= #{executEndTime, jdbcType=VARCHAR}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= now()
 WHERE BACKUP_RESULT_ID = #{backupResultId}
