mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.USER_ID AS "emplyrId" , A.USER_NM AS "emplyrNm" , B.IP_INFO AS "ipInfo" , B.LMTT_AT AS "lmttAt" FROM COMVN_USER_MASTER A LEFT OUTER JOIN COMTN_LOGIN_POLICY B ON A.USER_ID = B.EMPLYR_ID WHERE 1 = 1
AND A.USER_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

mapper.insert:

INSERT INTO COMTN_LOGIN_POLICY (
		EMPLYR_ID
      , IP_INFO
      , DPLCT_PERM_AT
      , LMTT_AT
      , FRST_REGISTER_ID
      , FRST_REGIST_PNTTM
      , LAST_UPDUSR_ID
      , LAST_UPDT_PNTTM
) VALUES (
        #{emplyrId}
      , #{ipInfo}
      , #{dplctPermAt}
      , #{lmttAt}
      , #{userId}
      , SYSDATE
      , #{userId}
      , SYSDATE
)

mapper.update:

UPDATE COMTN_LOGIN_POLICY 
   SET IP_INFO = #{ipInfo}
     , DPLCT_PERM_AT = #{dplctPermAt}
     , LMTT_AT = #{lmttAt}
     , LAST_UPDUSR_ID = #{userId}
     , LAST_UPDT_PNTTM = SYSDATE
 WHERE EMPLYR_ID = #{emplyrId}
