mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.CTSNN_ID AS "ctsnnId" , A.CTSNN_NM AS "ctsnnNm" , A.REQST_DE AS "reqstDe" , A.OCCRRNC_DE AS "occrrDe" , A.CONFM_AT AS "confmAt" , A.SANCTN_DT AS "sanctnDt" , C.USER_NM AS "usNm" , (select mst.USER_NM from COMVN_USER_MASTER mst where mst.ESNTL_ID = SANCTNER_ID ) AS "sanctnerNm" , (select code_nm from COMTC_CMMN_DETAIL_CODE where code_id ='COM054' and code = A.CTSNN_CODE ) AS "ctsnnCdNm" , (select info.ORGNZT_NM from COMTN_ORGNZT_INFO info where info.ORGNZT_ID = C.ORGNZT_ID ) AS "orgnztNm" FROM COMTN_CTSNN_MANAGE A LEFT OUTER JOIN COMVN_USER_MASTER C ON A.USER_ID = C.ESNTL_ID WHERE 1=1
AND A.CTSNN_CODE = #{searchKeyword}
AND A.OCCRRNC_DE BETWEEN #{searchFromDate} AND #{searchToDate}
AND C.USER_NM like '%'||#{searchNm}||'%'
AND A.CONFM_AT like '%'||#{searchConfmAt}||'%'
ORDER BY FRST_REGIST_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

SELECT COUNT(*) totcnt FROM COMTN_CTSNN_MANAGE A , COMVN_USER_MASTER C WHERE 1=1 AND A.USER_ID = C.ESNTL_ID
AND A.CTSNN_CODE = #{searchKeyword}
AND A.OCCRRNC_DE BETWEEN #{searchFromDate} AND #{searchToDate}
AND C.USER_NM like '%'||#{searchNm}||'%'
AND A.CONFM_AT like '%'||#{searchConfmAt}||'%'

mapper.insert:

            INSERT INTO COMTN_CTSNN_MANAGE (
