				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
            		#{ctsnnId}       
            	  , #{usid}           
            	  , #{ctsnnCd}        
            	  , #{reqstDe}        
            	  , #{ctsnnNm}        
            	  , #{trgterNm}       
