            	  , #{remark}         
            	  , #{sanctnerId}     
            	  , #{confmAt}        
            	  , #{infrmlSanctnId} 
				  , #{frstRegisterId}
				  , SYSDATE
				  , #{frstRegisterId} 
				  , SYSDATE
            )

mapper.update:

UPDATE COMTN_CTSNN_MANAGE
   SET CTSNN_NM         	= #{ctsnnNm}   
   	 , CTSNN_CODE         	= #{ctsnnCd}     
     , REQST_DE           	= #{reqstDe}     
     , TRGTER_NM         	= #{trgterNm}    
     , BRTHDY              	= #{brth}        
     , OCCRRNC_DE          	= #{occrrDe}    
     , RELATE            	= #{relate}      
     , RM            		= #{remark}      
     , LAST_UPDUSR_ID    	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 		= SYSDATE    
 WHERE CTSNN_ID          	= #{ctsnnId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.CTSNN_ID AS "ctsnnId" , A.REQST_DE AS "reqstDe" , A.CTSNN_NM AS "ctsnnNm" , A.OCCRRNC_DE AS "occrrDe" , A.CONFM_AT AS "confmAt" , A.SANCTN_DT AS "sanctnDt" , A.INFRML_SANCTN_ID AS "infrmlSanctnId" , C.USER_NM AS "usNm" , (select code_nm from COMTC_CMMN_DETAIL_CODE where code_id ='COM054' and code = A.CTSNN_CODE ) AS "ctsnnCdNm" , (select info.ORGNZT_NM from COMTN_ORGNZT_INFO info where info.ORGNZT_ID = C.ORGNZT_ID ) AS "orgnztNm" FROM COMTN_CTSNN_MANAGE A LEFT OUTER JOIN COMVN_USER_MASTER C ON A.USER_ID = C.ESNTL_ID WHERE A.SANCTNER_ID = #{sanctnerId}
AND A.CTSNN_CODE = #{searchKeyword}
AND A.OCCRRNC_DE BETWEEN #{searchFromDate} AND #{searchToDate}
AND C.USER_NM like '%'||#{searchNm}||'%'
AND A.CONFM_AT like '%'||#{searchConfmAt}||'%'
ORDER BY FRST_REGIST_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}


    SELECT COUNT(*) totcnt
      FROM COMTN_CTSNN_MANAGE A
         , COMVN_USER_MASTER C
     WHERE 1=1 
       AND A.SANCTNER_ID = #{sanctnerId}
       AND A.USER_ID = C.ESNTL_ID  


		 AND A.CTSNN_CODE = #{searchKeyword} 


		 AND A.OCCRRNC_DE BETWEEN #{searchFromDate} AND #{searchToDate} 


		 AND C.USER_NM like '%'||#{searchNm}||'%' 


		 AND A.CONFM_AT like '%'||#{searchConfmAt}||'%'

mapper.update:

UPDATE COMTN_CTSNN_MANAGE
   SET CONFM_AT           = #{confmAt}     
     , SANCTN_DT          = TO_DATE(#{sanctnDt}, 'YYYY-MM-DD HH24:MI:SS')
     , RETURN_RESN        = #{returnResn}  
     , LAST_UPDUSR_ID     = #{lastUpdusrId}
     , LAST_UPDT_PNTTM    = SYSDATE    
 WHERE CTSNN_ID           = #{ctsnnId}
