mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT b.ORGNZT_NM AS "orgnztNm" , a.ESNTL_ID AS "uniqId" , a.USER_NM AS "emplyrNm" , a.EMPL_NO AS "emplNo" , a.OFCPS_NM AS "ofcpsNm" FROM COMTN_EMPLYR_INFO a LEFT OUTER JOIN COMTN_ORGNZT_INFO b ON a.ORGNZT_ID = b.ORGNZT_ID WHERE 1=1
AND NVL(b.ORGNZT_NM, ' ') LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND NVL(a.USER_NM, ' ') LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY b.ORGNZT_NM, a.USER_NM ASC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

mapper.insert:

INSERT INTO COMTN_INFRML_SANCTN (
		INFRML_SANCTN_ID
	  , JOB_SE_CODE
	  , APPLCNT_ID
	  , REQST_DE
	  , SANCTNER_ID
	  , CONFM_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
) VALUES ( 
		#{infrmlSanctnId}
	  , #{jobSeCode}
	  , #{applcntId}
	  , #{reqstDe}
	  , #{sanctnerId}
	  , #{confmAt}
	  , #{frstRegisterId}
	  , SYSDATE
	  , #{frstRegisterId}
	  , SYSDATE
)

mapper.update:

UPDATE COMTN_INFRML_SANCTN
   SET SANCTNER_ID 		= #{sanctnerId}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= SYSDATE
 WHERE INFRML_SANCTN_ID = #{infrmlSanctnId}


UPDATE COMTN_INFRML_SANCTN
   SET CONFM_AT			= #{confmAt}
     , SANCTN_DT 		= sysdate
     , RETURN_RESN 		= #{returnResn}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= SYSDATE
 WHERE INFRML_SANCTN_ID = #{infrmlSanctnId}
