mapper.select:
/* 구현 Sql */
SELECT IMAGE_ID AS "imageId" , IMAGE_NM AS "imageNm" , IMAGE AS "image" , IMAGE_DC AS "imageDc" , REFLCT_AT AS "reflctAt" FROM COMTN_LOGIN_SCRIN_IMAGE WHERE 1=1
AND IMAGE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

            INSERT INTO COMTN_LOGIN_SCRIN_IMAGE (
            		IMAGE_ID
            	  , IMAGE_NM
            	  , IMAGE
            	  , IMAGE_FILE
            	  , IMAGE_DC
            	  , REFLCT_AT
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
            	    #{imageId}
            	  , #{imageNm}
            	  , #{image}
            	  , #{imageFile}
            	  , #{imageDc}
            	  , #{reflctAt}
            	  , #{userId}
            	  , sysdate()
            	  , #{userId}
            	  , sysdate()
            )

mapper.update:

UPDATE COMTN_LOGIN_SCRIN_IMAGE
   SET IMAGE_NM 		= #{imageNm}



     , IMAGE 			= #{image}
     , IMAGE_FILE 		= #{imageFile}



     , IMAGE_DC 		= #{imageDc}
     , REFLCT_AT 		= #{reflctAt}
     , LAST_UPDUSR_ID 	= #{userId}
     , LAST_UPDT_PNTTM 	= sysdate()
 WHERE IMAGE_ID = #{imageId}
