mapper.select:
/* 구현 Sql */
SELECT RECOMEND_SITE_ID AS "recomendSiteId" , RECOMEND_SITE_URL AS "recomendSiteUrl" , RECOMEND_SITE_NM AS "recomendSiteNm" , RECOMEND_CONFM_AT AS "recomendConfmAt" , CONFM_DE AS "confmDe" , FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_RECOMEND_SITE_INFO WHERE 1=1
AND TRGET_ID = #{trgetId}
AND RECOMEND_SITE_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND RECOMEND_SITE_URL LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:

INSERT INTO COMTN_RECOMEND_SITE_INFO (
		RECOMEND_SITE_ID
	  ,	RECOMEND_SITE_URL
	  ,	RECOMEND_SITE_NM
	  ,	RECOMEND_SITE_DC
  	  ,	RECOMEND_RESN_CN
	  ,	RECOMEND_CONFM_AT
	  ,	CONFM_DE
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
  	  ,	TRGET_ID
) VALUES ( 
		#{recomendSiteId}
	  , #{recomendSiteUrl}
	  , #{recomendSiteNm}
	  , #{recomendSiteDc}
	  , #{recomendResnCn}
	  , #{recomendConfmAt}
	  , #{confmDe}
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
  	  ,	#{trgetId}
)

mapper.update:

			UPDATE COMTN_RECOMEND_SITE_INFO	
			   SET RECOMEND_SITE_ID		= #{recomendSiteId}
				 , RECOMEND_SITE_URL	= #{recomendSiteUrl}
				 , RECOMEND_SITE_NM		= #{recomendSiteNm}
				 , RECOMEND_SITE_DC		= #{recomendSiteDc}
			  	 , RECOMEND_RESN_CN		= #{recomendResnCn}
				 , RECOMEND_CONFM_AT	= #{recomendConfmAt}
				 , CONFM_DE				= #{confmDe}
				 , LAST_UPDUSR_ID		= #{lastUpdusrId}
 				 , LAST_UPDT_PNTTM		= sysdate()
			 WHERE RECOMEND_SITE_ID = #{recomendSiteId}
