mapper.select:
/* 구현 Sql */
SELECT SITE_URL AS "siteUrl" , SITE_ID AS "siteId" , SITE_NM AS "siteNm" , B.CODE_NM AS "siteThemaClNm" , A.ACTVTY_AT AS "actvtyAt" , A.USE_AT AS "useAt" , USER_NM AS "emplyrNm" , A.LAST_UPDT_PNTTM AS "lastUpdusrPnttm" FROM COMTN_SITE_LIST A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM023') B ON A.SITE_THEMA_CL_CODE = B.CODE LEFT OUTER JOIN COMTN_EMPLYR_INFO C ON A.LAST_UPDUSR_ID = C.ESNTL_ID WHERE 1=1
AND A.SITE_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND A.SITE_URL LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY A.SITE_NM DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:


			INSERT INTO COMTN_SITE_LIST (
					SITE_ID
				  , SITE_URL 
 				  , SITE_NM 
				  , SITE_DC
				  , SITE_THEMA_CL_CODE 
				  , ACTVTY_AT 
				  , USE_AT
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
			) VALUES ( 
					#{siteId}
				  , #{siteUrl}
				  , #{siteNm}
				  , #{siteDc}
				  , #{siteThemaClCode}
				  , #{actvtyAt}
				  , #{useAt} 
				  , #{frstRegisterId}
				  , sysdate()
				  , #{frstRegisterId} 
				  , sysdate()
			)

mapper.update:

			UPDATE COMTN_SITE_LIST	
			   SET SITE_ID				= #{siteId}
				 , SITE_URL				= #{siteUrl} 
 				 , SITE_NM				= #{siteNm}
				 , SITE_DC				= #{siteDc}
				 , SITE_THEMA_CL_CODE	= #{siteThemaClCode} 
				 , ACTVTY_AT			= #{actvtyAt}
				 , USE_AT				= #{useAt}
				 , LAST_UPDUSR_ID		= #{lastUpdusrId}
				 , LAST_UPDT_PNTTM		= sysdate()
 			 WHERE SITE_ID = #{siteId}
