mapper.select:

            SELECT *
              FROM (
            SELECT ROWNUM RNUM, ALL_LIST.*
              FROM (

        /* 구현 Sql */

			SELECT FAQ_ID					AS "faqId"
			     , QESTN_SJ					AS "qestnSj"
			     , RDCNT					AS "inqireCo" 
			     , LAST_UPDT_PNTTM 			AS "lastUpdusrPnttm" 
		      FROM COMTN_FAQ_INFO
		     WHERE 1=1   			


		   		 AND TRGET_ID = #{trgetId} 




                 AND QESTN_SJ LIKE '%'|| #{searchKeyword} ||'%' 




		     ORDER BY FAQ_ID DESC								   

        /* 구현 Sql */

                   ) ALL_LIST
                   )
             WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

SELECT COUNT(*) totcnt FROM COMTN_FAQ_INFO WHERE 1=1
AND TRGET_ID = #{trgetId}
AND QESTN_SJ LIKE '%'|| #{searchKeyword} ||'%'

mapper.insert:


INSERT INTO COMTN_FAQ_INFO (
   		FAQ_ID
	  , QESTN_SJ
	  , QESTN_CN
	  , ANSWER_CN
	  , RDCNT
	  , ATCH_FILE_ID
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
  	  ,	TRGET_ID
) VALUES ( 
		#{faqId}
	  , #{qestnSj}
	  , #{qestnCn}
	  , #{answerCn}
	  , 0
	  , #{atchFileId, jdbcType=VARCHAR}
	  , #{frstRegisterId}
	  , SYSDATE
	  , #{frstRegisterId} 
	  , SYSDATE
  	  ,	#{trgetId}
)

mapper.update:

			UPDATE COMTN_FAQ_INFO	
			   SET RDCNT= NVL(RDCNT,0) + 1				  			
 			 WHERE FAQ_ID = #{faqId}


			UPDATE COMTN_FAQ_INFO	
			   SET FAQ_ID			= #{faqId} 
				 , QESTN_SJ			= #{qestnSj}
				 , QESTN_CN			= #{qestnCn}
				 , ANSWER_CN		= #{answerCn}
				 , ATCH_FILE_ID		= #{atchFileId, jdbcType=VARCHAR}				  			
				 , LAST_UPDUSR_ID	= #{lastUpdusrId}
				 , LAST_UPDT_PNTTM  = SYSDATE
 			 WHERE FAQ_ID = #{faqId}
