mapper.select:
/* 구현 Sql */
SELECT A.QA_ID AS "qaId" , A.QESTN_SJ AS "qestnSj" , A.WRTER_NM AS "wrterNm" , A.WRITNG_DE AS "writngDe" , A.RDCNT AS "inqireCo" , B.CODE_NM AS "qnaProcessSttusCodeNm" FROM COMTN_QA_INFO A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM028') B ON A.QNA_PROCESS_STTUS_CODE = B.CODE WHERE 1=1
AND A.TRGET_ID = #{trgetId}
AND A.WRTER_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.QESTN_SJ LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.QA_ID DESC
/* 구현 Sql */
LIMIT #recordPerPage# OFFSET #firstIndex#

mapper.insert:

INSERT INTO COMTN_QA_INFO (
		QA_ID
	  , QESTN_SJ
	  , QESTN_CN
	  , WRITNG_PASSWORD
	  , AREA_NO
	  , MIDDLE_TELNO
	  , END_TELNO
	  ,	EMAIL_ADRES
	  , EMAIL_ANSWER_AT
	  , WRTER_NM
	  , WRITNG_DE
	  , RDCNT
	  , QNA_PROCESS_STTUS_CODE
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
  	  ,	TRGET_ID
) VALUES ( 
		#{qaId}
	  , #{qestnSj}
	  , #{qestnCn}
	  , #{writngPassword}
	  , #{areaNo}
	  , #{middleTelno}
	  , #{endTelno}
	  , #{emailAdres}
	  , IFNULL(#{emailAnswerAt, jdbcType=VARCHAR},'N')
	  , #{wrterNm}
	  , DATE_FORMAT(sysdate(), '%Y%m%d')
	  , 0
	  , 1
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
  	  ,	#{trgetId}
)

mapper.update:

			UPDATE COMTN_QA_INFO	
			   SET RDCNT= IFNULL(RDCNT,0) + 1
 			 WHERE QA_ID = #{qaId}

mapper.select:

SELECT COUNT(*) totcnt
  FROM COMTN_QA_INFO
 WHERE QA_ID = #{qaId}
   AND WRITNG_PASSWORD = #{writngPassword}

mapper.update:

			UPDATE COMTN_QA_INFO	
			   SET QESTN_SJ			= #{qestnSj}
				 , QESTN_CN			= #{qestnCn}
				 , WRITNG_PASSWORD	= #{writngPassword}
				 , AREA_NO			= #{areaNo}
				 , MIDDLE_TELNO		= #{middleTelno}
				 , END_TELNO		= #{endTelno}
				 , EMAIL_ADRES		= #{emailAdres}
				 , EMAIL_ANSWER_AT	= IFNULL(#{emailAnswerAt, jdbcType=VARCHAR},'N')
				 , WRTER_NM			= #{wrterNm}
				 , WRITNG_DE		= DATE_FORMAT(sysdate(), '%Y%m%d')
                 , LAST_UPDUSR_ID   = #{lastUpdusrId}    
                 , LAST_UPDT_PNTTM  = sysdate()    
			 WHERE QA_ID = #{qaId}

mapper.select:
/* 구현 Sql */
SELECT A.QA_ID AS "qaId" , A.QESTN_SJ AS "qestnSj" , A.WRTER_NM AS "wrterNm" , A.WRITNG_DE AS "writngDe" , A.RDCNT AS "inqireCo" , B.CODE_NM AS "qnaProcessSttusCodeNm" FROM COMTN_QA_INFO A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM028') B ON A.QNA_PROCESS_STTUS_CODE = B.CODE WHERE 1=1
AND A.TRGET_ID = #{trgetId}
AND A.WRTER_NM LIKE '%'|| #{searchKeyword} ||'%'
AND B.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.QESTN_SJ DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE COMTN_QA_INFO	
   SET QNA_PROCESS_STTUS_CODE	=#{qnaProcessSttusCode}
	 , ANSWER_CN				=#{answerCn}
	 , ANSWER_DE				=DATE_FORMAT(sysdate(), '%Y%m%d')
	 , LAST_UPDUSR_ID			=#{lastUpdusrId}
	 , LAST_UPDT_PNTTM			=sysdate()
 WHERE QA_ID = #{qaId}
