mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.INDVDL_INFO_POLICY_ID AS "indvdlInfoId" , A.INDVDL_INFO_POLICY_NM AS "indvdlInfoNm" , A.INDVDL_INFO_POLICY_AGRE_AT AS "indvdlInfoYn" , (SELECT USER_NM FROM COMVN_USER_MASTER WHERE ESNTL_ID = A.FRST_REGISTER_ID ) AS "frstRegisterNm" , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_INDVDL_INFO_POLICY A WHERE 1=1
AND INDVDL_INFO_POLICY_NM LIKE '%'|| #{searchKeyword} ||'%'
AND INDVDL_INFO_POLICY_CN LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:


INSERT INTO COMTN_INDVDL_INFO_POLICY ( 
		INDVDL_INFO_POLICY_ID
	  ,	INDVDL_INFO_POLICY_NM
	  ,	INDVDL_INFO_POLICY_CN
	  ,	INDVDL_INFO_POLICY_AGRE_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
)VALUES( 
		#{indvdlInfoId}
	  ,	#{indvdlInfoNm}
	  ,	#{indvdlInfoDc}
	  ,	#{indvdlInfoYn}
	  , #{frstRegisterId}
	  , SYSDATE
	  , #{frstRegisterId} 
	  , SYSDATE
)

mapper.update:

UPDATE COMTN_INDVDL_INFO_POLICY 
   SET INDVDL_INFO_POLICY_NM 		= #{indvdlInfoNm}
     , INDVDL_INFO_POLICY_CN 		= #{indvdlInfoDc}
     , INDVDL_INFO_POLICY_AGRE_AT 	= #{indvdlInfoYn}
     , LAST_UPDUSR_ID 				= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 				= SYSDATE
 WHERE INDVDL_INFO_POLICY_ID = #{indvdlInfoId}
