mapper.select:
/* 구현 Sql */
SELECT A.USE_STPLAT_ID AS "useStplatId" , A.USE_STPLAT_NM AS "useStplatNm" , (SELECT USER_NM FROM COMVN_USER_MASTER WHERE ESNTL_ID = A.FRST_REGISTER_ID ) AS "frstRegisterNm" , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_STPLAT_INFO A WHERE 1=1
AND USE_STPLAT_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND USE_STPLAT_CN LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY USE_STPLAT_NM DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:


INSERT INTO COMTN_STPLAT_INFO (
		USE_STPLAT_ID
	  , USE_STPLAT_NM				   
	  , USE_STPLAT_CN
	  , INFO_PROVD_AGRE_CN
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
) VALUES ( 
		#{useStplatId}
	  , #{useStplatNm}			
	  , #{useStplatCn}
	  , #{infoProvdAgreCn}
	  , #{frstRegisterId}
	  , now()
	  , #{frstRegisterId} 
	  , now()
)

mapper.update:

			UPDATE COMTN_STPLAT_INFO	
			   SET USE_STPLAT_NM		= #{useStplatNm}
				 , USE_STPLAT_CN		= #{useStplatCn}				  
