mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.DATA_SOURC_NM , A.SERVER_NM , A.DBMS_KND AS "dbmsKind" , A.CECK_SQL , A.MNGR_NM , A.MNGR_EMAIL_ADRES AS "mngrEmailAddr" , A.MNTRNG_STTUS , TO_CHAR(A.CREAT_DT, 'YYYY-MM-DD HH24:MI:SS') AS "creatDt" , B.CODE_NM AS "dbmsKindNm" , C.CODE_NM AS "mntrngSttusNm" FROM COMTN_DB_MNTRNG A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM048') B ON A.DBMS_KND = B.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM046') C ON A.MNTRNG_STTUS = C.CODE WHERE 1=1
AND A.DATA_SOURC_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.SERVER_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.MNGR_NM LIKE '%'|| #{searchKeyword} ||'%'
AND C.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.DATA_SOURC_NM ASC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

            INSERT INTO COMTN_DB_MNTRNG (
                	DATA_SOURC_NM
                  ,	SERVER_NM
                  ,	DBMS_KND
                  ,	CECK_SQL
                  ,	MNGR_NM
                  ,	MNGR_EMAIL_ADRES
                  ,	MNTRNG_STTUS
                  ,	CREAT_DT
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
                    #{dataSourcNm}
                  , #{serverNm}
                  , #{dbmsKind}
                  , #{ceckSql}
                  , #{mngrNm}
                  , #{mngrEmailAddr}
                  , #{mntrngSttus}
                  , SYSDATE
				  , #{frstRegisterId}
				  , SYSDATE
				  , #{frstRegisterId} 
				  , SYSDATE
            )

mapper.update:

UPDATE COMTN_DB_MNTRNG 
   SET SERVER_NM = #{serverNm}
   	 , DBMS_KND = #{dbmsKind}
   	 , CECK_SQL = #{ceckSql}
   	 , MNGR_NM = #{mngrNm}
   	 , MNGR_EMAIL_ADRES = #{mngrEmailAddr}
   	 , MNTRNG_STTUS = #{mntrngSttus}
   	 , CREAT_DT = SYSDATE
   	 , LAST_UPDUSR_ID = #{lastUpdusrId}
   	 , LAST_UPDT_PNTTM = SYSDATE
 WHERE DATA_SOURC_NM = #{dataSourcNm}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.LOG_ID , A.DATA_SOURC_NM , A.SERVER_NM , A.MNGR_NM , A.MNGR_EMAIL_ADRES AS "mngrEmailAddr" , TO_CHAR(A.CREAT_DT, 'YYYY-MM-DD HH24:MI:SS') AS "creatDt" , B.CODE_NM AS "dbmsKindNm" , C.CODE_NM AS "mntrngSttusNm" FROM COMTN_DB_MNTRNG_LOG A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM048') B ON A.DBMS_KND = B.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM046') C ON A.MNTRNG_STTUS = C.CODE WHERE 1=1
AND TO_CHAR(A.CREAT_DT, 'YYYYMMDD') >= #{searchKeywordFrom}
AND TO_CHAR(A.CREAT_DT, 'YYYYMMDD') <= #{searchKeywordTo}
AND A.DATA_SOURC_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.SERVER_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.MNGR_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.LOG_ID DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

SELECT COUNT(*) totcnt FROM COMTN_DB_MNTRNG_LOG A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM048') B ON A.DBMS_KND = B.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM046') C ON A.MNTRNG_STTUS = C.CODE WHERE 1=1
AND TO_CHAR(A.CREAT_DT, 'YYYYMMDD') >= #{searchKeywordFrom}
AND TO_CHAR(A.CREAT_DT, 'YYYYMMDD') <= #{searchKeywordTo}
AND A.DATA_SOURC_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.SERVER_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.MNGR_NM LIKE '%'|| #{searchKeyword} ||'%'

mapper.insert:

            INSERT INTO COMTN_DB_MNTRNG_LOG (
                	LOG_ID
                  ,	DATA_SOURC_NM
                  ,	SERVER_NM
                  ,	DBMS_KND
                  ,	CECK_SQL
                  ,	MNGR_NM
                  ,	MNGR_EMAIL_ADRES
                  ,	MNTRNG_STTUS
                  ,	CREAT_DT
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
                  ,	LOG_INFO
            ) VALUES (
                    #{logId}
                  , #{dataSourcNm}
                  , #{serverNm}
                  , #{dbmsKind}
                  , #{ceckSql}
                  , #{mngrNm}
                  , #{mngrEmailAddr}
                  , #{mntrngSttus}
                  , SYSDATE
				  , #{frstRegisterId}
				  , SYSDATE
				  , #{frstRegisterId} 
				  , SYSDATE
                  , #{logInfo, jdbcType=VARCHAR}
            )
