mapper.select:
/* 구현 Sql */
SELECT A.SYS_ID , A.WEBSVC_KND WEB_KIND , A.SITE_URL , B.CODE_NM HTTP_STTUS_CD , DATE_FORMAT(A.CREAT_DT, '%Y-%m-%d %H:%i:%S') CREAT_DT , A.MNGR_NM , A.MNGR_EMAIL_ADRES MNGR_EMAIL_ADDR FROM COMTN_HTTP_MON A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM072') B ON A.HTTP_STTUS_CODE = B.CODE WHERE 1 = 1
AND A.MNGR_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND B.CODE_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY A.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:

