mapper.update:

UPDATE COMTN_HTTP_MON 
   SET WEBSVC_KND         = #{webKind}
     , SITE_URL           = #{siteUrl}
   	 , MNGR_NM        	  = #{mngrNm}
     , MNGR_EMAIL_ADRES   = #{mngrEmailAddr}
     , LAST_UPDUSR_ID     = #{lastUpdusrId}
     , LAST_UPDT_PNTTM    = sysdate()
 WHERE SYS_ID             = #{sysId}


UPDATE COMTN_HTTP_MON 
   SET HTTP_STTUS_CODE   = #{httpSttusCd}
     , CREAT_DT          = STR_TO_DATE(#{creatDt}, '%Y%m%d%H%i%S')
     , LAST_UPDUSR_ID    = #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	 = sysdate()
 WHERE SYS_ID            = #{sysId}

mapper.select:
/* 구현 Sql */
SELECT A.LOG_ID , A.SYS_ID , A.WEBSVC_KND WEB_KIND , B.SITE_URL , C.CODE_NM HTTP_STTUS_CD , DATE_FORMAT(A.CREAT_DT, '%Y-%m-%d %H:%i:%S') CREAT_DT , A.LOG_INFO , A.MNGR_NM , A.MNGR_EMAIL_ADRES MNGR_EMAIL_ADDR FROM COMTN_HTTP_MON_LOG A LEFT OUTER JOIN COMTN_HTTP_MON B ON A.SYS_ID = B.SYS_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM072') C ON A.HTTP_STTUS_CODE = C.CODE WHERE 1=1
AND DATE_FORMAT(CREAT_DT, '%Y-%m-%d %H%i%S') BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND A.WEBSVC_KND LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND C.CODE_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY A.CREAT_DT DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:

    INSERT INTO  COMTN_HTTP_MON_LOG (  
    	   LOG_ID
         , SYS_ID
         , WEBSVC_KND
         , SITE_URL
         , HTTP_STTUS_CODE
         , CREAT_DT
         , LOG_INFO		         
         , MNGR_NM
         , MNGR_EMAIL_ADRES
		 , FRST_REGISTER_ID 
		 , FRST_REGIST_PNTTM 
		 , LAST_UPDUSR_ID
		 , LAST_UPDT_PNTTM 
    ) VALUES (  
    	   #{logId}
         , #{sysId}
         , #{webKind}
         , #{siteUrl}
         , #{httpSttusCd}		         
         , STR_TO_DATE(#{creatDt}, '%Y%m%d%H%i%S')
         , #{logInfo, jdbcType=VARCHAR}		         
         , #{mngrNm}
         , #{mngrEmailAddr}
		 , #{frstRegisterId}
		 , sysdate()
		 , #{frstRegisterId}
		 , sysdate()
    )
