mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT SN , BULD_NM , LA , LO , TELNO , ADRES FROM MBLTN_GEO_LOCATION WHERE 1 = 1
AND BULD_NM LIKE '%'|| #{searchKeyword} ||'%'
AND TELNO LIKE '%'|| #{searchKeyword} ||'%'
AND ADRES LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY sn DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO MBLTN_GEO_LOCATION (
		SN
	  , MBER_ID
	  , BULD_NM
	  , LA
	  , LO
	  , TELNO
	  , ADRES
) VALUES (
		#{sn}
	  , #{mberId}
	  , #{buldNm}
	  , #{la}
	  , #{lo}
	  , #{telno}
	  , #{adres}
)

mapper.update:

			UPDATE MBLTN_GEO_LOCATION
			   SET BULD_NM = #{buldNm}
			     , LA 	   = #{la}
			     , LO 	   = #{lo}
			     , TELNO   = #{telno}
			     , ADRES   = #{adres}
 			 WHERE SN = #{sn}


 				 AND MBER_ID = #{mberId}

mapper.delete:

			DELETE 
			  FROM MBLTN_GEO_LOCATION 
			 WHERE SN = #{sn}


 				 AND MBER_ID = #{mberId}
