mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT SN , MBER_ID , XAXIS , YAXIS , LGD_NM , CREAT_DT , UPDT_DT FROM MBLTN_CHART_GRAPH WHERE 1=1
AND MBER_ID LIKE '%'|| #{searchKeyword} ||'%'
AND XAXIS LIKE '%'|| #{searchKeyword} ||'%'
AND YAXIS LIKE '%'|| #{searchKeyword} ||'%'
AND LGD_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY SN DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO MBLTN_CHART_GRAPH (
		SN
	  , MBER_ID
	  , XAXIS
	  , YAXIS
	  , LGD_NM
	  , CREAT_DT
	  , UPDT_DT
) VALUES ( 
		#{sn}
	  , #{mberId}
	  , #{xaxis}
	  , #{yaxis}
	  , #{lgdNm}
	  , SYSDATE
	  , SYSDATE 
)

mapper.update:

			UPDATE MBLTN_CHART_GRAPH 
			   SET SN      = #{sn} 
				 , XAXIS   = #{xaxis}
				 , YAXIS   = #{yaxis}
				 , LGD_NM  = #{lgdNm}
				 , UPDT_DT = SYSDATE
 			 WHERE SN = #{sn}


 				 AND MBER_ID = #{mberId}

mapper.delete:

			DELETE 
			  FROM MBLTN_CHART_GRAPH 
			 WHERE SN = #{sn}


 				 AND MBER_ID = #{mberId}
