mapper.select:
/* 구현 Sql */
SELECT A.SN , A.MBER_ID , A.MLTMD_CODE , B.CODE_NM AS MLTMD_NM , A.MLTMD_SJ , A.ATCH_FILE_ID , A.BROWSER_NM , A.CREAT_DT , A.UPDT_DT FROM MBLTN_MLTMD A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM077') B ON A.MLTMD_CODE = B.CODE WHERE 1=1
AND A.MBER_ID LIKE '%'|| #{searchKeyword} ||'%'
AND B.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.MLTMD_SJ LIKE '%'|| #{searchKeyword} ||'%'
AND A.BROWSER_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY SN DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO MBLTN_MLTMD (
		SN
	  , MBER_ID
	  , MLTMD_CODE
	  , MLTMD_SJ
	  , ATCH_FILE_ID
	  , BROWSER_NM
	  , CREAT_DT
	  , UPDT_DT
) VALUES ( 
		#{sn}
	  , #{mberId}
	  , #{mltmdCode}
	  , #{mltmdSj}
	  , #{atchFileId}
	  , #{browserNm}
	  , sysdate()
	  , sysdate() 
)

mapper.update:

			UPDATE MBLTN_MLTMD 
			   SET SN			= #{sn} 
				 , MLTMD_CODE	= #{mltmdCode}
				 , MLTMD_SJ		= #{mltmdSj}
				 , ATCH_FILE_ID	= #{atchFileId}
				 , BROWSER_NM	= #{browserNm}
				 , UPDT_DT		= sysdate()
 			 WHERE SN = #{sn}


 				 AND MBER_ID = #{mberId}

mapper.delete:

			DELETE 
			  FROM MBLTN_MLTMD 
			 WHERE SN = #{sn}


 				 AND MBER_ID = #{mberId}
