SELECT SN , MBER_ID , PHOTO_SJ , ATCH_FILE_ID , CREAT_DT , UPDT_DT FROM MBLTN_PHOTO_ALBUM WHERE 1=1
AND MBER_ID LIKE '%'|| #{searchKeyword} ||'%'
AND PHOTO_SJ LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY SN DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

