mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.SN , A.MBER_ID , A.OFFLINE_SJ , A.OFFLINE_CN , TO_CHAR( A.CREAT_DT, 'YYYY-MM-DD') AS CREAT_DT , TO_CHAR( A.UPDT_DT, 'YYYY-MM-DD') AS UPDT_DT FROM MBLTN_OFFLINE_WEB A WHERE 1 = 1
AND A.MBER_ID LIKE '%'|| #{searchKeyword} ||'%'
AND A.OFFLINE_SJ LIKE '%'|| #{searchKeyword} ||'%'
AND ((A.MBER_ID LIKE '%' || #{searchKeyword} || '%') OR (A.OFFLINE_SJ LIKE '%' || #{searchKeyword} || '%') OR (A.OFFLINE_CN LIKE '%' || #{searchKeyword} || '%'))
ORDER BY SN DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO MBLTN_OFFLINE_WEB (
		SN
	  , MBER_ID
	  , OFFLINE_SJ
	  ,	OFFLINE_CN
	  ,	CREAT_DT
	  ,	UPDT_DT
) VALUES (
		#{sn}
	  ,	#{mberId}
	  ,	#{offlineWebSj}
	  ,	#{offlineWebCn}
	  ,	SYSDATE
	  ,	SYSDATE
)

mapper.update:

UPDATE MBLTN_OFFLINE_WEB
   SET MBER_ID 		= #{mberId}
     , OFFLINE_SJ 	= #{offlineWebSj}
     , OFFLINE_CN 	= #{offlineWebCn}
     , UPDT_DT 		= sysdate
 WHERE SN = #{sn}	   


    AND MBER_ID = #{mberId}

mapper.delete:

DELETE 
  FROM MBLTN_OFFLINE_WEB
 WHERE SN = #{sn}


    AND MBER_ID = #{mberId}
