mapper.select:
/* 구현 Sql */
SELECT A.SN , A.MBER_ID , A.OFFLINE_SJ , A.OFFLINE_CN , DATE_FORMAT(A.CREAT_DT, '%Y%m%d') AS CREAT_DT , DATE_FORMAT(A.UPDT_DT, '%Y%m%d') AS UPDT_DT FROM MBLTN_OFFLINE_WEB A WHERE 1 = 1
AND A.MBER_ID LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND A.OFFLINE_SJ LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND ((A.MBER_ID LIKE '%' || #{searchVO.searchKeyword} || '%') OR (A.OFFLINE_SJ LIKE '%' || #{searchVO.searchKeyword} || '%') OR (A.OFFLINE_CN LIKE '%' || #{searchVO.searchKeyword} || '%'))
ORDER BY SN DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:

INSERT INTO MBLTN_OFFLINE_WEB (
		SN
	  , MBER_ID
	  , OFFLINE_SJ
	  ,	OFFLINE_CN
	  ,	CREAT_DT
	  ,	UPDT_DT
) VALUES (
		#{sn}
	  ,	#{mberId}
	  ,	#{offlineWebSj}
	  ,	#{offlineWebCn}
	  ,	now()
	  ,	now()
)

mapper.update:

			UPDATE MBLTN_OFFLINE_WEB
			   SET MBER_ID 		= #{mberId}
			     , OFFLINE_SJ 	= #{offlineWebSj}
			     , OFFLINE_CN 	= #{offlineWebCn}
			     , UPDT_DT 		= now()
			 WHERE SN=#{sn}	   


 			    AND MBER_ID = #{mberId}

mapper.delete:

			DELETE 
			  FROM MBLTN_OFFLINE_WEB
			 WHERE SN = #{sn}


 			    AND MBER_ID = #{mberId}
