mapper.select:
/* 구현 Sql */
SELECT A.SN , A.MBER_ID , A.SYNC_SJ , A.SYNC_CN , DATE_FORMAT(A.SYNC_DT, '%Y%m%d') AS SYNC_DT , DATE_FORMAT(A.CREAT_DT, '%Y%m%d') AS CREAT_DT , DATE_FORMAT(A.UPDT_DT, '%Y%m%d') AS UPDT_DT FROM MBLTN_SYNC A WHERE 1 = 1
AND A.MBER_ID LIKE '%'|| #{searchKeyword} ||'%'
AND A.SYNC_SJ LIKE '%'|| #{searchKeyword} ||'%'
AND ( (A.MBER_ID LIKE '%' || #{searchKeyword} || '%') OR (A.SYNC_SJ LIKE '%' || #{searchKeyword} || '%') OR (A.SYNC_CN LIKE '%' || #{searchKeyword} || '%'))
ORDER BY SN DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO MBLTN_SYNC (
		SN
	  , MBER_ID
	  ,	SYNC_SJ
	  ,	SYNC_CN
	  , CREAT_DT
	  ,	UPDT_DT
) VALUES (
		#{sn}
	  ,	#{mberId}
	  ,	#{syncSj}
	  ,	#{syncCn}
	  ,	sysdate()
	  ,	sysdate()
)

mapper.update:

			UPDATE MBLTN_SYNC
			   SET MBER_ID = #{mberId}
			     , SYNC_SJ = #{syncSj}
			     , SYNC_CN = #{syncCn}
			     , UPDT_DT = sysdate()
			 WHERE SN = #{sn}	   


 			    AND MBER_ID = #{mberId}

mapper.delete:

			DELETE 
			  FROM MBLTN_SYNC
			 WHERE SN = #{sn}


 			    AND MBER_ID = #{mberId}
